import React, { Component } from 'react';
import './App.css';

class App extends Component {

  state = {
    search: '',
    searchListItems: [],
    item1: [],
    item2: [],
    data: [],
    cal1: [],
    vitE1: [],
    item1Card: false,
    item2Card: false,
    cal2: [],
    vitE2: [],
  }

  // componentDidMount(){
    // fetch('https://api.nal.usda.gov/ndb/reports/?ndbno=01009&type=f&format=json&api_key=deSFRe23fryJ5XJpEjRJwqWhVGkfFWzXWvpymagG')

    // .then(resp=>resp.json())
    // .then(resp=>{
    //   // console.log(resp);
    //   this.setState({
    //     data: resp.report.food
    //   })      
    // })

    // fetch('https://api.nal.usda.gov/ndb/nutrients/?format=json&api_key=deSFRe23fryJ5XJpEjRJwqWhVGkfFWzXWvpymagG&nutrients=205&nutrients=204&nutrients=208&nutrients=269&nutrients=401&nutrients=323&nutrients=328&ndbno=01009')
    // fetch('https://api.nal.usda.gov/ndb/nutrients/?format=json&api_key=KqBwC25CfgRxoNXgvoA3ej3Qxv205mji8YsVpvgo&nutrients=203&nutrients=204&nutrients=205&nutrients=401&ndbno=45243630')
    // .then(resp=>resp.json())
    // .then(resp=>{
    //   // console.log(resp);
    //   if(resp.report.foods[0].nutrients.length > 0 ){
    //     this.setState({
    //       // item1: resp.report.foods[0],
    //       cal: Math.round(resp.report.foods[0].nutrients[2].gm * 4),
    //       vitE: resp.report.foods[0].nutrients[0].gm,
    //     })
    //   }
    // })
  // }

  onSearch = e => {
    this.setState({
      search: e.target.value
    })
  }

  onSearchSubmit = e => {
    e.preventDefault();

    //handle if user doesn't enter item-->throw error
    if (!this.state.search) {
      //eventually render this message
      console.log('please enter item');
    } else {

    //make fetch call to search for item 1 and 2
    //get just for first 10-15 or all??
    //validate the this.state.search isn't empty
    //how to handle if user's item doesn't exists (non-food itemex. 'rain')
      // let searchItem;
      //  if(this.state.search === regex-alpha numeric??)
    fetch(`https://api.nal.usda.gov/ndb/search/?format=json&q=${this.state.search}&sort=n&max=5&offset=0&api_key=deSFRe23fryJ5XJpEjRJwqWhVGkfFWzXWvpymagG`)
      .then(resp => resp.json())
      .then(data => {
        console.log(data);
        this.setState({
          searchListItems: data.list.item
        })
        // want to create li's to list returned item
        //store data in var listItems then use conditional render
      })
      .catch(error => console.log(error))
    }
  }

  onPagination = () => {
    //grab next 10-15 list items...or fetch all then store all data and just render 10-15 at a time...
    //function that know what array index(dynamic) to start at
    // 16-30? 31-45. 46-50
    //each time button is pressed add 15 to a counter. sure.
  }

  checkValues = (nutrient) => {
    if(Number.isInteger(nutrient)){
      return nutrient;
    } else {
      return 0;
    }
  }

  onChooseItem = (e) => {
    //fetch that items data
    const { item1Card, searchListItems } = this.state;
    const item = searchListItems.filter(item => e.target.getAttribute('name') === item.name);
    const itemId = item[0].ndbno;

    if (item1Card) {
      fetch(`https://api.nal.usda.gov/ndb/nutrients/?format=json&api_key=KqBwC25CfgRxoNXgvoA3ej3Qxv205mji8YsVpvgo&nutrients=203&nutrients=204&nutrients=205&nutrients=208&nutrients=269&nutrients=323&ndbno=${itemId}`)
      .then(resp=>resp.json())
      .then(resp=>{
        console.log(resp);
        // if(resp.report.foods[0].nutrients.length > 0){
        this.setState({
          item2: resp.report.foods[0],
          cal2: this.checkValues(resp.report.foods[0].nutrients[2].gm)*4
              + this.checkValues(resp.report.foods[0].nutrients[4].gm)*9
              + this.checkValues(resp.report.foods[0].nutrients[5].gm)*4,
          vitE2: resp.report.foods[0].nutrients[0].gm,
          item2Card: true
        })
      })
     } else {
    // fetch(`https://api.nal.usda.gov/ndb/nutrients/?format=json&api_key=deSFRe23fryJ5XJpEjRJwqWhVGkfFWzXWvpymagG&nutrients=205&nutrients=204&nutrients=208&nutrients=269&nutrients=401&nutrients=323&nutrients=328&ndbno=${itemId}`)
    fetch(`https://api.nal.usda.gov/ndb/nutrients/?format=json&api_key=KqBwC25CfgRxoNXgvoA3ej3Qxv205mji8YsVpvgo&nutrients=203&nutrients=204&nutrients=205&nutrients=208&nutrients=269&nutrients=323&ndbno=${itemId}`)
    .then(resp=>resp.json())
    .then(resp=>{
      console.log(resp);
      this.setState({
        item1: resp.report.foods[0],
        cal1: this.checkValues(Math.round(resp.report.foods[0].nutrients[2].gm))*4
              + this.checkValues(Math.round(resp.report.foods[0].nutrients[4].gm))*9
              + this.checkValues(Math.round(resp.report.foods[0].nutrients[5].gm)) * 4,
        vitE1: resp.report.foods[0].nutrients[0].gm,
        item1Card: true
      })
    })
  }
    //store data for item 2
  }


  // fetchChosenItem = (itemId) = {
  //   //store data for item 1 (use if/else)
  //   fetch('https://api.nal.usda.gov/ndb/nutrients/?format=json&api_key=deSFRe23fryJ5XJpEjRJwqWhVGkfFWzXWvpymagG&nutrients=205&nutrients=204&nutrients=208&nutrients=269&nutrients=401&nutrients=323&nutrients=328&ndbno=01009')
  //   .then(resp=>resp.json())
  //   .then(resp=>{
  //     console.log(resp);
  //     this.setState({
  //       cal: Math.round(resp.report.foods[0].nutrients[0].gm * 4),
  //       vitE: resp.report.foods[0].nutrients[0].gm,
  //     })    
  //   })
  // }

  onCompare = () => {
    //get all data for search item 1 and 2?? No, do in search
    //compare item 1 and 2
  }

  render() {
    // console.log(this.state.nutrientdata.sr)
    console.log(`item1:` + this.state.item1)
    const { searchListItems, data, item1, cal1, vitE1, vitE2, item2 } = this.state;

    let calorieCalc;
    if (item1.length > 0){
      calorieCalc = Math.round(this.state.item1.nutrients[4].gm * 4);
      return calorieCalc
    }

    return (
      <div className="App">
        <header className="App-header">
          <h1>USDA</h1>
        </header>
        <form className='searchfield'>
            <input onChange={e=>this.onSearch(e)} type='search' style={{width: '100vw'}} placeholder='enter food item'/>
            <input type='submit' onClick={e=>this.onSearchSubmit(e)}/>
        </form>
        <ul onClick={e=>this.onChooseItem(e)}>
        {
          searchListItems.length > 0
          ? searchListItems.map(item => (
            <li
              key={item.ndbno}
              name={item.name}
            >{item.name}
            </li>
          ))
          : ''
        }
        </ul>
        <main style={{display: 'flex'}}>
        { this.state.item1Card &&
          (<div className="display1" style={{ width: '350px', height: '300px'}}>
              <h2>{item1.name}</h2>
              <p>{data.fg}</p>
              <p>calories: {cal1}</p>

              <p>vitamin E: {vitE1}</p>
            </div>)
        }
        { this.state.item2Card &&
          (<div className="display2" style={{ width: '350px', height: '300px'}}>
              <h2>{item2.name}</h2>
              <p>{data.fg}</p>

              <p>vitamin E: {vitE2}</p>
            </div>)
        }
        </main>
      </div>
    );
  }
}

export default App;


