import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Sidebar from './Sidebar';
import { Form, FormGroup, Button, Label, Input, ListGroup, ListGroupItem } from 'reactstrap';
import './App.css';

class App extends Component {

  state = {
    search: '',
    searchListItems: [],
    item1: [],
    item2: [],
    // data: [],
    cal1: [],
    vitE1: [],
    fat1: [],
    sugar1: [],
    item1Card: false,
    item2Card: false,
    cal2: [],
    vitE2: [],
    fat2: [],
    sugar2: [],
    openSideBar: false,
    compareBtnClicked: false
  }

  onSearch = e => {
    this.setState({
      search: e.target.value
    })
  }

  onSearchSubmit = e => {
    e.preventDefault();

    //handle if user doesn't enter item-->throw error
    if (!this.state.search) {
      //eventually render this message
      console.log('please enter item');
    } else {

    //make fetch call to search for item 1 and 2
    //get just for first 10-15 or all??
    //validate the this.state.search isn't empty
    //how to handle if user's item doesn't exists (non-food itemex. 'rain')
      // let searchItem;
      //  if(this.state.search === regex-alpha numeric??)
    fetch(`https://api.nal.usda.gov/ndb/search/?format=json&q=${this.state.search}&sort=n&max=5&offset=0&api_key=deSFRe23fryJ5XJpEjRJwqWhVGkfFWzXWvpymagG`)
      .then(resp => resp.json())
      .then(data => {
        console.log(data);
        this.setState({
          searchListItems: data.list.item
        })
        // want to create li's to list returned item
        //store data in var listItems then use conditional render
      })
      .catch(error => console.log(error))
    }
  }

  onPagination = () => {
    //grab next 10-15 list items...or fetch all then store all data and just render 10-15 at a time...
    //function that knows what array index(dynamic) to start at
    // 16-30? 31-45. 46-50
    //each time button is pressed add 15 to a counter.
  }

  checkValues = (nutrient) => {
    if(nutrient === "--"){
      return 0;
    } else {
      return nutrient;
    }
  }

  onChooseItem = (e) => {
    //fetch that items data
    const { item1Card, searchListItems } = this.state;
    const item = searchListItems.filter(item => e.target.getAttribute('name') === item.name);
    const itemId = item[0].ndbno;

    if (item1Card) {
      fetch(`https://api.nal.usda.gov/ndb/nutrients/?format=json&api_key=KqBwC25CfgRxoNXgvoA3ej3Qxv205mji8YsVpvgo&nutrients=203&nutrients=204&nutrients=205&nutrients=208&nutrients=269&nutrients=323&ndbno=${itemId}`)
      .then(resp=>resp.json())
      .then(resp=>{
        console.log(resp);
        // if(resp.report.foods[0].nutrients.length > 0){
        this.setState({
          item2: resp.report.foods[0],
          cal2: resp.report.foods[0].nutrients[0].value,
          vitE2: resp.report.foods[0].nutrients[2].value,
          fat2: resp.report.foods[0].nutrients[4].value,
          sugar1: resp.report.foods[0].nutrients[3].value,
          item2Card: true
        })
      })
     } else {
    // fetch(`https://api.nal.usda.gov/ndb/nutrients/?format=json&api_key=deSFRe23fryJ5XJpEjRJwqWhVGkfFWzXWvpymagG&nutrients=205&nutrients=204&nutrients=208&nutrients=269&nutrients=401&nutrients=323&nutrients=328&ndbno=${itemId}`)
    fetch(`https://api.nal.usda.gov/ndb/nutrients/?format=json&api_key=KqBwC25CfgRxoNXgvoA3ej3Qxv205mji8YsVpvgo&nutrients=203&nutrients=204&nutrients=205&nutrients=208&nutrients=269&nutrients=323&ndbno=${itemId}`)
    .then(resp=>resp.json())
    .then(resp=>{
      console.log(resp);
      this.setState({
        item1: resp.report.foods[0],
        cal1: resp.report.foods[0].nutrients[0].value,
        vitE1: resp.report.foods[0].nutrients[2].value,
        fat1: resp.report.foods[0].nutrients[4].value,
        sugar2: resp.report.foods[0].nutrients[3].value,
        item1Card: true,
        openSideBar: true
      })
    })
  }
    //store data for item 2
  }


  // fetchChosenItem = (itemId) = {

  // }

  onCompare = () => {
    //get all data for search item 1 and 2?? No, do in search
    //compare item 1 and 2
  }

  render() {
    // console.log(this.state.nutrientdata.sr)
    console.log(`item1:` + this.state.item1)
    const { searchListItems, data, item1, cal1, cal2, vitE1, vitE2, item2, fat1, fat2, sugar1, sugar2 } = this.state;

    // let calorieCalc;
    // if (item1.length > 0){
    //   calorieCalc = Math.round(this.state.item1.nutrients[4].gm * 4);
    //   return calorieCalc
    // }

    return (
      <div className="App">
        <header className="App-header">
          <h1>Enter Food</h1>
        </header>
        <Form inline className='searchfield'>
          <FormGroup>
            <Label for='search'>
              <Input id='search' className='search-text' onChange={e=>this.onSearch(e)} type='search' placeholder='enter food item'/>
              <Input className='search-btn' type='submit' value='Search' onClick={e=>this.onSearchSubmit(e)}/>
            </Label>
          </FormGroup>
        </Form>
        <ListGroup
          className='list-group'
          onClick={e=>this.onChooseItem(e)}>
        {
          searchListItems.length > 0
          ? searchListItems.map(item => (
            <ListGroupItem
              className='list-group-item'
              key={item.ndbno}
              name={item.name}
            >{item.name}
            </ListGroupItem>
          ))
          : ''
        }
        </ListGroup>
        { this.state.openSideBar
          ? <Sidebar
              item1={item1}
              item2={item2}
            />
          : ''
        }
        <main style={{display: 'flex'}}>
        { this.state.item1Card &&
          (<div className="display1" style={{ width: '350px', height: '300px'}}>
              <h2>{item1.name}</h2>
              <p>calories: {cal1}</p>
              <p>fat: {fat1}grams</p>
              <p>vitamin E: {vitE1}</p>
              <p>sugar: {sugar1}g</p>
            </div>)
        }
        { this.state.item2Card &&
          (<div className="display2" style={{ width: '350px', height: '300px'}}>
              <h2>{item2.name}</h2>
              <p>calories: {cal2}</p>
              <p>fat: {fat2}grams</p>
              <p>vitamin E: {vitE2}</p>
              <p>sugar: {sugar2}g</p>
            </div>)
        }
        </main>
      </div>
    );
  }
}

export default App;


